mapper.select:
SELECT menu_id
FROM MENUS
WHERE MENU_ID = #{ menu_id }

mapper.insert:
INSERT INTO BOARD (BNO, MENU_ID, TITLE, CONTENT, WRITER)
VALUES ((SELECT NVL(MAX(BNO),0) + 1 FROM BOARD), #{ menu_id}, #{ title
}, #{ content }, #{ writer } )

mapper.update:
UPDATE BOARD
SET TITLE = #{title}, content = #{content}, writer = #{ writer }
WHERE BNO = #{ bno }

UPDATE BOARD
SET HIT = HIT + 1
WHERE BNO = #{ bno }
